import React, { useState } from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { generateShortCode, validateUrl } from "../utils/urlUtils";
import { logEvent } from "./logger";

export default function UrlForm({ onShorten }) {
  const [url, setUrl] = useState("");
  const [validity, setValidity] = useState("");
  const [customCode, setCustomCode] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateUrl(url)) {
      alert("Invalid URL format!");
      logEvent("Invalid URL attempt", { url });
      return;
    }

    const code = customCode || generateShortCode();
    const expiry = validity ? parseInt(validity) : 10; // default 10 mins
    const newShortUrl = {
      id: code,
      originalUrl: url,
      shortUrl: `http://localhost:3000/${code}`,
      expiry: new Date(Date.now() + expiry * 60000).toLocaleString(),
      clicks: [],
    };

    logEvent("URL Shortened", { shortUrl: newShortUrl.shortUrl });
    onShorten(newShortUrl);

    setUrl("");
    setValidity("");
    setCustomCode("");
  };

  return (
    <Card className="w-full shadow-lg rounded-2xl">
      <CardContent className="p-6">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label className="font-medium">Enter Long URL</Label>
            <Input
              type="text"
              placeholder="https://example.com/very/long/url"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label className="font-medium">Validity (minutes)</Label>
              <Input
                type="number"
                placeholder="10"
                value={validity}
                onChange={(e) => setValidity(e.target.value)}
              />
            </div>

            <div>
              <Label className="font-medium">Custom Shortcode</Label>
              <Input
                type="text"
                placeholder="my-link"
                value={customCode}
                onChange={(e) => setCustomCode(e.target.value)}
              />
            </div>
          </div>

          <Button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 rounded-lg transition-all"
          >
            Shorten URL
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}
